    transform:scale(0.1); 
    -moz-transform:scale(0.1); 
    -webkit-transform:scale(0.1); 
    -moz-transform-origin:top left; 
    -webkit-transform-origin:top left; 
  } 
} 
.stl_layer { }
.stl_ie { font-size: 1pt; } 
.stl_ie body { font-size: 12em; } 
@media print{
  .stl_view {
    font-size:1em; 
    transform:scale(1);
  }
}

/* Optimize page container without sidebar */
#page-container {
  position: relative;
  left: 0 !important;
  width: 100% !important;
  height: auto !important;
  overflow: visible !important;
  background-color: white;
  margin: 0 auto;
  max-width: 1200px;
  padding: 20px;
  box-sizing: border-box;
}

/* Page frame optimization */
.pf {
  position: relative;
  background-color: white;
  overflow: visible;
  margin: 0 auto 20px auto;
  border: none;
  page-break-after: auto;
  page-break-inside: avoid;
}

/* Page content optimization */
.pc {
  position: relative;
  border: 0;
  padding: 0;
  margin: 0;
  top: 0;
  left: 0;
  width: 100%;
  height: auto;
  overflow: visible;
  display: block;
  transform-origin: 0 0;
  -ms-transform-origin: 0 0;
  -webkit-transform-origin: 0 0;
}

.pc.opened { display: block; }

.bf {
  position: absolute;
  border: 0;
  margin: 0;
  top: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  -ms-user-select: none;
  -moz-user-select: none;
  -webkit-user-select: none;
  user-select: none;
}

.bi {
  position: absolute;
  border: 0;
  margin: 0;
  -ms-user-select: none;
  -moz-user-select: none;
  -webkit-user-select: none;
  user-select: none;
}

.c {
  position: absolute;
  border: 0;
  padding: 0;
  margin: 0;
  overflow: hidden;
  display: block;
}

.t {
  position: absolute;
  white-space: pre;
  font-size: 1px;
  transform-origin: 0 100%;
  -ms-transform-origin: 0 100%;
  -webkit-transform-origin: 0 100%;
  unicode-bidi: bidi-override;
  -moz-font-feature-settings: "liga" 0;
}

.t:after { content: ''; }
.t:before { content: ''; display: inline-block; }
.t span { position: relative; unicode-bidi: bidi-override; }
._ { display: inline-block; color: transparent; z-index: -1; }

::selection { background: rgba(127,255,255,0.4); }
::-moz-selection { background: rgba(127,255,255,0.4); }

.pi { display: none; }
.d {
  position: absolute;
  transform-origin: 0 100%;
  -ms-transform-origin: 0 100%;
  -webkit-transform-origin: 0 100%;
}

.it { border: 0; background-color: rgba(255,255,255,0.0); }
.ir:hover { cursor: pointer; }

/* Print optimization */
@media print {
  @page { margin: 0; }
  html { margin: 0; }
  body { margin: 0; -webkit-print-color-adjust: exact; }
  #page-container {
    width: auto;
    height: auto;
    overflow: visible;
    background-color: transparent;
    max-width: none;
    padding: 0;
  }
  .d { display: none; }
  .pf {
    margin: 0;
    box-shadow: none;
    page-break-after: always;
    page-break-inside: avoid;
  }
}

/* Responsive design */
@media screen and (max-width: 768px) {
  #page-container {
    padding: 10px;
    max-width: 100%;
  }
}


