Prism.manual = true;
// https://github.com/PrismJS/prism/issues/832
Prism.hooks.add('before-highlight', env => &#123;
  env.code = env.element.innerText;
&#125;);
const version = &#123;
    highlight: '11.4.0',
    prism: '1.26.0',
    themes: '1.9.0'
&#125;;

const source = &#123;
    api: 'https://data.jsdelivr.com/v1/package/npm/',
    src: 'https://fastly.jsdelivr.net/npm/'
&#125;;

class Style &#123;
    constructor() &#123;
        this.origin = &#123;
            highlight: &#123;
                src: `highlight.js@$&#123;version.highlight&#125;`,
                dir: '/styles/'
            &#125;,
            prism: &#123;
                src: `prismjs@$&#123;version.prism&#125;`,
                dir: '/themes/'
            &#125;,
            themes: &#123;
                src: `prism-themes@$&#123;version.themes&#125;`,
                dir: '/themes/'
            &#125;
        &#125;;
        this.link = document.createElement('link');
        this.link.rel = 'stylesheet';
        document.head.appendChild(this.link);
    &#125;
    async fetch(name) &#123;
        let target = this.origin[name];
        if (target.cache) return target.cache;
        let response = await fetch(source.api + target.src + '/flat');
        let data = await response.json();
        target.cache = data.files.filter(item => item.name.startsWith(target.dir) && item.name.endsWith('.css'));
        console.log(target.cache.length + ' themes loaded.');
        return target.cache;
    &#125;
    async getAllStyles(name) &#123;
        let data = await this.fetch(name);
        let target = this.origin[name];
        return data.map(item => [source.src + target.src + item.name, item.name.replace(target.dir, '').replace('.css', '')]);
    &#125;
    getStyle(href) &#123;
        this.link.href = href;
    &#125;
&#125;

class Code &#123;
    constructor() &#123;
        ;
    &#125;
    hexo(engine, version) &#123;
        if (version === 'v7.0.0+') &#123;
            return `syntax_highlighter: $&#123;engine === 'highlight' ? 'highlightjs' : 'prismjs'&#125;
highlight:
  ...
prismjs:
  ...`;
        } else {
            return `highlight:
  enable: $&#123;engine === 'highlight'&#125;
  ...
prismjs:
  enable: $&#123;engine !== 'highlight'&#125;
  ...`;
        }
    }
    next(engine, theme) {
        let content;
        if (engine === 'highlight') {
            content = `theme:
    light: ${theme}
    dark: ${theme}`;
        } else {
            content = `prism:
    light: ${theme}
    dark: ${theme}`;
        }
        return `codeblock:
# Code Highlight theme
# All available themes: https://theme-next.js.org/highlight/
  ...
  $&#123;content&#125;
  ...
`;
    }
}

const CODE = `struct Person {
    name: String,
    age: u32,
}

impl Person {
    fn new(name: String, age: u32) -> Person {
        Person { name, age }
    }

    fn show(&self) {
        println!("Name: {}, Age: {}", self.name, self.age);
    }

    fn set_name(&mut self, new_name: String) {
        self.name = new_name;
    }

    fn set_age(&mut self, new_age: u32) {
        self.age = new_age;
    }
}

fn main() {
    let mut person = Person::new("Alice".to_string(), 30);
    person.show();
}`;

class Controller {
    constructor() {
        this.style = new Style();
        this.code = new Code();
        this.element = {
            engine: document.getElementById('engine'),
            version: document.getElementById('version'),
            theme: document.getElementById('theme'),
            language: document.getElementById('language'),
            code: document.getElementById('code')
        };
        this.value = {
            engine: localStorage.getItem('engine') || 'highlight',
            version: localStorage.getItem('version') || 'v7.0.0+',
            theme: localStorage.getItem('theme') || 'tomorrow',
            language: localStorage.getItem('language') || 'rust',
            code: localStorage.getItem('code') || CODE
        };
        this.onchange = {
            engine: async () => {
                let styles = await this.style.getAllStyles(this.value.engine);
                let html;
                if (this.value.engine === 'highlight') {
                    styles = styles.filter(item => !unavailable.includes(item[1]));
                    html = this.options(styles);
                } else {
                    let extra = await this.style.getAllStyles('themes');
                    html = `
                        ${this.options(styles)}


                        ${this.options(extra)}
                    `;
                }
                let { theme } = this.element;
                theme.innerHTML = html;
                theme.selectedIndex = this.value.theme;
                if (theme.selectedIndex === -1) theme.selectedIndex = 0;
                this.update('theme');
            },
            version: () => {},
            theme: () => {
                let href = this.element.theme.value;
                this.style.getStyle(href);
            },
            language: () => {},
            code: () => {}
        };
        Object.keys(this.element).forEach(key => {
            this.element[key].value = this.value[key];
            this.element[key].addEventListener('input', () => {
                this.update(key);
            });
        });
        this.onchange.engine();
    }
    options(array) {
        return array.map(item => {
            let [value, name] = item;
            return `${name}`;
        }).join('');
    }
    update(key) {
        this.value[key] = key === 'theme' ? this.element[key].selectedIndex : this.element[key].value;
        localStorage.setItem(key, this.value[key]);
        this.onchange[key]();
        this.render();
    }
    render() {
        document.querySelector('.hexo').innerHTML = this.code.hexo(this.value.engine, this.value.version);
        document.querySelector('.next').innerHTML = this.code.next(this.value.engine, this.element.theme[this.value.theme].text);
        const preview = document.querySelector('#preview .render code');
        preview.className = `language-${this.value.language}`;
        // Escape html
        preview.textContent = this.value.code;
        document.querySelectorAll('.render code').forEach(element => {
            this.value.engine === 'highlight' ? hljs.highlightElement(element) : Prism.highlightElement(element);
        });
    }
}

const controller = new Controller();

/*-----------------------------------*\
  $RESET
\*-----------------------------------*/

*, :before, :after {
  margin: 0;
  padding: 0;
  position: relative;
  -webkit-box-sizing: border-box;
     -moz-box-sizing: border-box;
          box-sizing: border-box;
}

input, select, button, textarea {
  -webkit-appearance: none;
     -moz-appearance: none;
          appearance: none;

  font: inherit;
  color: inherit;
}

/*-----------------------------------*\
  $OBJECTS
\*-----------------------------------*/

.butt, .input {
  padding: .75rem;
  margin: .375rem;

  background-color: transparent;
  border-radius: 4px;
}

  .butt:focus, .input:focus {
    outline: none;
  }

.butt {
  border: 2px solid #f35626;
  line-height: 1.375;
  padding-left: 1.5rem;
  padding-right: 1.5rem;

  font-weight: 700;

  color: #f35626;

  cursor: pointer;
  -webkit-animation: hue 60s infinite linear;
}

  .butt--primary {
    background-color: #f35626;
    color: #fff;
  }

.input {
  border: 1px solid #c0c8c9;
  border-radius: 4px;
}

  .input--dropdown {
    background-image: url("images/ddown.png");
    background-image: url("images/ddown.svg?3"), none;
    background-repeat: no-repeat;
    background-size: 1.5rem 1rem;
    background-position: right center;
  }

/*-----------------------------------*\
  $TYPOGRAPHY
\*-----------------------------------*/

h1, .alpha {
  margin-bottom: 1.5rem;

  font-size: 3rem;
  font-weight: 100;
  line-height: 1;
  letter-spacing: -.05em;
}

h2, .beta {
  margin-bottom: .75rem;

  font-weight: 400;
  font-size: 1.5rem;
  line-height: 1;
}

@media (min-width: 650px) {
  .mega {
    font-size: 6rem;
    line-height: 1;
  }
}

.subhead, .meta {
  color: #7b8993;
}

.promo {
  text-align: center;
}

p, hr, form {
  margin-bottom: 1.5rem;
}

hr {
  border: none;
  margin-top: -1px;
  height: 1px;
  background-color: #c0c8c9;
  background-image: -webkit-linear-gradient(0deg, #fff, #c0c8c9, #fff);
}

a {
  color: inherit;
  text-decoration: underline;
  -webkit-animation: hue 60s infinite linear;
}

  a:hover {
    color: #f35626;
  }

/*-----------------------------------*\
  $LAYOUT
\*-----------------------------------*/

.wrap {
  max-width: 38rem;
  margin: 0 auto;
}

.island {
  padding: 1.5rem;
}

.isle {
  padding: .75rem;
}

.spit {
  padding: .375rem;
}

/*-----------------------------------*\
  $BASE
\*-----------------------------------*/

html {
  font: 100%/1.5 "Roboto", Verdana, sans-serif;
  color: #3d464d;
  background-color: #fff;
  -webkit-font-smoothing: antialiased;
  width: 100%;

  overflow: hidden-x;

  /* Centering in The Unknown */
  text-align: center;
}

@media (min-width: 650px) {
  html {
    height: 100%;
  }

  html:before {
    content: '';
    display: inline-block;
    height: 100%;
    vertical-align: middle;
    margin-right: -0.25em;
  }

  body {
    display: inline-block;
    vertical-align: middle;
    max-width: 38rem;
  }
}

/*-----------------------------------*\
  $HEADER
\*-----------------------------------*/

.site__header {
  -webkit-animation: bounceInUp 1s;
}

  .site__title {
    color: #f35626;
    background-image: -webkit-linear-gradient(92deg,#f35626,#feab3a);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    -webkit-animation: hue 60s infinite linear;
  }

.site__content {
  -webkit-animation: bounceInUp 1s;
  -webkit-animation-delay: .1s;
}

  .site__content form {
    -webkit-animation: bounceInUp 1s;
    -webkit-animation-delay: .1s;
  }

/*-----------------------------------*\
  $ANIMATIONS
\*-----------------------------------*/

@-webkit-keyframes hue {
  from {
    -webkit-filter: hue-rotate(0deg);
  }

  to {
    -webkit-filter: hue-rotate(-360deg);
  }
}

Elegant and Powerful Theme for HexoTheme NexT has the following features:

Vanilla JavaScript.
Support native dark mode.
Support Pjax to deliver a fast browsing experience.
Integrated MathJax and KaTeX mathematical formula rendering.
Integrated PDF.js and mermaid diagram.
Support Disqus, Changyan, LiveRe, Gitalk, Utterances and Isso comment systems.
Support a large number of third-party statistics services.
Easy to switch CDN provider.
Easy to customize content or modify style.

Four Schemes in OneBy using different Schemes in NexT, you can simply change the style of your blog, and nearly all options can be used in all schemes.

.image-comparison-container {
    position: relative;
}

.image-comparison {
    opacity: 1 !important;
}

@keyframes next-clip-path {
    from {
        clip-path: polygon(0 0, 0 0, 0 100%, 0 100%);
    }
    to {
        clip-path: polygon(0 0, 100% 0, 100% 100%, 0 100%);
    }
}

.image-comparison:last-of-type {
    animation: next-clip-path 8s alternate ease-in-out infinite;
    position: absolute;
    top: 0;
}






Multilingual SupportThanks to the contribution of our users and developers, NexT now supports over 20 languages.

